<?phpinclude_once dirname( dirname( __FILE__ ) ) . '/andy/simple_html_dom.php';$keyword = urlencode( $_REQUEST['keyword'] );?><div class="row">	<?php	$i    = 0;	$url  = 'http://' . $_REQUEST['url'] . '/search?tbm=isch&site=imghp&num=30&q=' . $keyword;	$html = file_get_html( $url );	foreach ( $html->find( '.images_table td' ) as $ele ):$i ++; ?>		<div class="col-sm-6 col-md-4 col-lg-4">			<div class="img-con">				<a><?php					$img_url = $ele->find( 'a img', 0 )->src;					echo $ele->find( 'a', 0 )->innertext; //images					?></a>				<?php				$href                           = $ele->find( 'a', 0 )->href; //store url				$ele->find( 'a', 0 )->outertext = ''; //remove a tag				echo $ele->innertext; //get desc				?>				<input value="<?php echo $i; ?>@@#@@<?php echo 'http://' . $_REQUEST['url'] . '/' .				                                               $href; ?>@@#@@<?php echo $img_url ?>" type="checkbox" name="img[]" id="">			</div>		</div>	<?php endforeach; ?></div>